import React, { useState } from 'react';
import {
  Paper,
  TextField,
  Button,
  Box,
  Typography,
  Alert,
  Chip,
  FormControlLabel,
  Switch
} from '@mui/material';
import { Link as LinkIcon, ContentCopy } from '@mui/icons-material';
import axios from 'axios';

const UrlForm = ({ onUrlCreated }) => {
  const [formData, setFormData] = useState({
    originalUrl: '',
    customAlias: '',
    description: '',
    tags: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [shortenedUrl, setShortenedUrl] = useState(null);
  const [useCustomAlias, setUseCustomAlias] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const payload = {
        originalUrl: formData.originalUrl,
        description: formData.description,
        tags: formData.tags ? formData.tags.split(',').map(tag => tag.trim()) : []
      };

      if (useCustomAlias && formData.customAlias) {
        payload.customAlias = formData.customAlias;
      }

      const response = await axios.post('/api/urls/shorten', payload);

      setShortenedUrl(response.data.url);
      setSuccess('URL shortened successfully!');

      // Reset form
      setFormData({
        originalUrl: '',
        customAlias: '',
        description: '',
        tags: ''
      });
      setUseCustomAlias(false);

      // Notify parent component
      if (onUrlCreated) {
        onUrlCreated(response.data.url);
      }
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to shorten URL');
    }

    setLoading(false);
  };

  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      setSuccess('URL copied to clipboard!');
    } catch (error) {
      setError('Failed to copy URL');
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
      <Typography variant="h5" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
        <LinkIcon />
        Shorten URL
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }}>
          {success}
        </Alert>
      )}

      {shortenedUrl && (
        <Paper 
          elevation={1} 
          sx={{ 
            p: 2, 
            mb: 2, 
            backgroundColor: '#f8f9fa',
            border: '1px solid #e9ecef'
          }}
        >
          <Typography variant="subtitle2" gutterBottom>
            Shortened URL:
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Typography 
              variant="body1" 
              sx={{ 
                fontFamily: 'monospace',
                backgroundColor: 'white',
                p: 1,
                borderRadius: 1,
                flexGrow: 1,
                border: '1px solid #ddd'
              }}
            >
              {shortenedUrl.shortUrl}
            </Typography>
            <Button
              variant="outlined"
              size="small"
              startIcon={<ContentCopy />}
              onClick={() => copyToClipboard(shortenedUrl.shortUrl)}
            >
              Copy
            </Button>
          </Box>
        </Paper>
      )}

      <Box component="form" onSubmit={handleSubmit}>
        <TextField
          fullWidth
          label="Enter URL to shorten"
          name="originalUrl"
          value={formData.originalUrl}
          onChange={handleChange}
          placeholder="https://example.com/very-long-url"
          required
          sx={{ mb: 2 }}
        />

        <FormControlLabel
          control={
            <Switch
              checked={useCustomAlias}
              onChange={(e) => setUseCustomAlias(e.target.checked)}
            />
          }
          label="Use custom alias"
          sx={{ mb: 2 }}
        />

        {useCustomAlias && (
          <TextField
            fullWidth
            label="Custom Alias (optional)"
            name="customAlias"
            value={formData.customAlias}
            onChange={handleChange}
            placeholder="my-custom-link"
            helperText="Leave empty for auto-generated short ID"
            sx={{ mb: 2 }}
          />
        )}

        <TextField
          fullWidth
          label="Description (optional)"
          name="description"
          value={formData.description}
          onChange={handleChange}
          placeholder="Brief description of this URL"
          multiline
          rows={2}
          sx={{ mb: 2 }}
        />

        <TextField
          fullWidth
          label="Tags (optional)"
          name="tags"
          value={formData.tags}
          onChange={handleChange}
          placeholder="work, project, important (comma separated)"
          helperText="Separate tags with commas"
          sx={{ mb: 3 }}
        />

        <Button
          type="submit"
          variant="contained"
          fullWidth
          size="large"
          disabled={loading}
          sx={{ py: 1.5 }}
        >
          {loading ? 'Shortening...' : 'Shorten URL'}
        </Button>
      </Box>
    </Paper>
  );
};

export default UrlForm;
